mapper.select:
select count(*) from structrual_dictionary_mapping where 1=1

	and uuid=#{uuid,jdbcType=INTEGER}


	and definition_type=#{definitionType,jdbcType=VARCHAR}


	and definition_name=#{definitionName,jdbcType=VARCHAR}


	and structrual_dictionary_name=#{structrualDictionaryName,jdbcType=VARCHAR}


	and target_dictionary_name=#{targetDictionaryName,jdbcType=VARCHAR}


	and create_ts=#{createTs,jdbcType=TIMESTAMP}


	and create_user=#{createUser,jdbcType=VARCHAR}


	and last_mod_ts=#{lastModTs,jdbcType=TIMESTAMP}


	and last_mod_user=#{lastModUser,jdbcType=VARCHAR}
